import { Component, OnInit } from '@angular/core';

// import { ThemeService } from './services/theme.service';
// import { FontSizeService } from './services/font-size.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  title = 'a11y';

  constructor() {}

  ngOnInit(): void {}
}
<a href="#main" class="mx-4 mx-xxl-5 mt-2 p-1 skip-to-content" [tabIndex]="0">{{
  "home.mainContent" | translate
}}</a>
<app-header></app-header>
<main id="main" class="container-fluid mt-2 p-0 cb1 mt-4">
  <section class="features cb2 lh-0">
    <app-a11y-options></app-a11y-options>
    <router-outlet></router-outlet>
  </section>
</main>
<app-footer></app-footer>
