import { Component, OnInit } from '@angular/core';
import { FontSizeService } from 'src/app/services/font-size/font-size.service';
import { LinkHighlightService } from 'src/app/services/links-hightligh/links-highlight.service';
import { ThemeService } from 'src/app/services/themes-color/theme.service';

@Component({
  selector: 'app-a11y-options',
  templateUrl: './a11y-options.component.html',
  styleUrls: ['./a11y-options.component.scss'],
})
export class A11yOptionsComponent {
  //Default and reset font size
  // font_size = 16;

  ////////////////////////////
  //Font Size Increade Decrease
  ////////////////////////////

  constructor(
    private themeService: ThemeService,
    public fontSizeService: FontSizeService,
    public linkHighlightService: LinkHighlightService
  ) {}

  ngOnInit(): void {}

  get fontSize() {
    return this.fontSizeService.getFontSize();
  }

  increaseFontSize() {
    this.fontSizeService.setFontSize(this.fontSize + 5);
  }

  decreaseFontSize() {
    this.fontSizeService.setFontSize(this.fontSize - 5);
  }

  resetFontSize() {
    this.fontSizeService.resetFontSize();
  }

  //////////////
  //Toogle theme
  //////////////

  //Toogle theme button
  switchTheme(theme: string): void {
    this.themeService.setTheme(
      theme as 'light' | 'dark' | 'creamy' | 'blind-color'
    );
  }

  toggleTheme(): void {
    this.themeService.toggleTheme();
  }

  //////////////
  //linkHighlightService
  //////////////

  get buttonText() {
    return this.linkHighlightService.highlightedLinksCount > 0
      ? 'Remove Highlights'
      : 'Highlight Links';
  }

  toggleLinkHighlight() {
    this.linkHighlightService.toggleLinkHighlight();
  }
}
<div
  class="offcanvas offcanvas-start"
  data-bs-backdrop="static"
  tabindex="-1"
  id="staticBackdrop"
  aria-labelledby="staticBackdropLabel"
>
  <div class="offcanvas-header">
    <h5 class="offcanvas-title" id="staticBackdropLabel">Offcanvas</h5>
    <button
      type="button"
      class="btn-close"
      data-bs-dismiss="offcanvas"
      aria-label="Close"
    ></button>
  </div>
  <div class="offcanvas-body">
    <div>I will not close if you click outside of me.</div>

    <div class="container">
      <div class="text-center">
        <button (click)="toggleLinkHighlight()">
          {{ buttonText }}
        </button>

        <p>
          {{ fontSize }}% ({{ fontSizeService.getMinFontSize() }}% -
          {{ fontSizeService.getMaxFontSize() }}%)
        </p>
        <button (click)="decreaseFontSize()">MENOS -</button>
        <button (click)="increaseFontSize()">MAS +</button>
        <button (click)="resetFontSize()">Reset</button>
        <p>Welcome to the Angular theme switcher with Bootstrap!</p>
        <button class="btn btn-primary" (click)="switchTheme('light')">
          Light Mode
        </button>
        <button class="btn btn-dark" (click)="switchTheme('dark')">
          Dark Mode
        </button>
        <button class="btn btn-warning" (click)="switchTheme('creamy')">
          Creamy Mode
        </button>
        <button class="btn btn-danger" (click)="switchTheme('blind-color')">
          Blind-Color Mode
        </button>
        <br /><br />
        <button class="btn btn-info" (click)="toggleTheme()">
          Toggle Theme
        </button>
      </div>
    </div>
  </div>
</div>
