@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent {

}
<footer
  class="container-fluid bg-dark text-light text-center footer mt-auto pt-2 bg-primary position-fixed bottom-0"
